const Item = (props) => {
  const { item } = props;

  const removeItemHandler = (id) => {
    props.onRemoveItem(id);
  };

  return (
    <li>
      <input type="checkbox" />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.description}
      </span>
      <button onClick={() => removeItemHandler(item.id)}>&times;</button>
    </li>
  );
};

export default Item;
